<template>
    <div>
        {{ visible ? value.toString() : ''}}
    </div>
</template>

<script>
export default {
    props: ['value', 'visible', 'emitter', 'ikey', 'putData'],
    mounted() {
        if (this.ikey) this.putData(this.ikey, this.value)
        this.emitter.trigger('process')
    },
}
</script>

