/* eslint-disable no-console */
import fs from 'fs'
import { resolve } from 'path'
import jsonFile from 'jsonfile'
import promisify from 'es6-promisify'

const writeFile = promisify(fs.writeFile)
const writeJson = promisify(jsonFile.writeFile)

const fixers = {
    'has readme file': ({ root, pkg, contents }) => {
        console.log('Creating [readme.md]...')
        return writeFile(resolve(root, pkg.dir, 'readme.md'), contents)
    },
}

export default ({ root, package: pkg, fixes }) => {
    console.log('Creating [package.json]...')
    return Promise.all([
        ...Object.entries(fixes).map(([fix, details]) =>
            fixers[fix]({ root, pkg, ...details })
        ),
        writeJson(
            resolve(root, pkg.dir, 'package.json'),
            { version: '0.0.0' },
            { spaces: 4 }
        ),
    ])
}
