import { prompt, Separator } from 'inquirer'
import { cyan, red, green } from 'chalk'

export default ({ staged = [], modified = [], untracked = [] }) => {
    let choices = []

    if (staged.length > 0) {
        choices = choices.concat([
            ...staged.map(file => ({
                name: green(file),
                value: file,
                checked: true,
            })),
            new Separator(),
        ])
    }

    if (modified.length > 0) {
        choices = choices.concat([
            ...modified.map(file => ({
                name: red(file),
                value: file,
            })),
            new Separator(),
        ])
    }

    if (untracked.length > 0) {
        choices = choices.concat([
            ...untracked.map(file => ({
                name: cyan(file),
                value: file,
            })),
            new Separator(),
        ])
    }

    return prompt([
        {
            type: 'checkbox',
            name: 'filesToCommit',
            message: 'What files will we commit?',
            choices,
        },
    ]).then(({ filesToCommit }) => filesToCommit)
}
