const formatPackage = ({ type, scopes, subject }) => `
    <change>
        <type>${type}</type>
        <scope>(${scopes.join(',')})<scope>: 
        <subject>${subject}</subject>
    </change>
`

export default () => ({ changes }) => `
    <bar>-</bar>
    <h1>Changes</h1>
    <bar>-</bar>
    <list>${changes
        .sort(() => 1)
        .map(formatPackage)
        .join('')}</list>
`
