export default ({ name, fixes }) => `
    <bar>-</bar>
    <h1>Prereleasing [${name}]</h1>
    <bar>-</bar>
    <list>
    ${Object.entries(fixes).map(([type]) => {
        if (type === 'has readme file')
            return `
                <check><success>A readme will be created.</success></check>
            `

        return ''
    })}
        <check><success>A version will be added.</success></check>
    </list>
`
