const templates = {
    'has readme file': 'A readme will be created.',
    'has a version number': 'A version will be added.',
}

export default ({ name, fixes }) => `
    <bar>-</bar>
    <h1>Prereleasing [${name}]</h1>
    <bar>-</bar>
    <list>
    ${Object.entries(fixes)
        .map(([type]) => `<check><success>${templates[type]}</success></check>`)
        .join('')}
    </list>
`
