import { Input } from 'rete'
import log from '@dev-xp/log'
import socket from '@sigils/sockets'
import component from '@sigils/components'

export default {
    number: name => (altar, key) => {
        const input = new Input(key, name, socket.number)
        input.addControl(component.number()(altar, key))
        return input
    },
}
