import log from '@dev-xp/log'
import out from '@sigils/outputs'
import component from '@sigils/components'
import createRune from '@sigils/create-rune'

export default ({ name = 'Number', output = 'Number' } = {}) =>
    createRune({
        name,
        out: { num: out.number(output) },
        components: { num: component.number() },
        magic: ({ rune, outputs }) => {
            outputs.num = rune.data.num
        },
    })
