import component from '@sigils/components'
import createRune from '@sigils/create-rune'

export const t = ({ name = 'True' } = {}) =>
    createRune({ name, params: [component.const(true, false)] })

export const f = ({ name = 'False' } = {}) =>
    createRune({ name, params: [component.const(false, false)] })
