import * as _ from '@dev-xp/utils'

// eslint-disable-next-line complexity
export default (path, sideEffect) => value => {
    const finalPath = sideEffect ? path : null
    const finalSideEffect = sideEffect || path
    const finalValue = finalPath ? _.getProp(finalPath, value) : value
    let result = finalSideEffect
    if (typeof finalSideEffect === 'function')
        result = finalSideEffect(finalValue)
    return Promise.resolve(result).then(() => value)
}
