import serialize from 'pretty-format';

export default (suite, subject, examples) => {
    const tests = [];
    const it = (test, setup) => {
        let inputs = [];
        const given = args => {
            inputs = [...inputs, ...args];
        };
        setup(given);
        inputs.forEach((input, index) =>
            tests.push({
                test,
                index,
                input,
            }),
        );
    };
    examples(it);
    const results = tests.map(test => {
        const result = subject(...test.input);
        return {
            ...test,
            result,
            snapshot: serialize(result),
        };
    });
    return results;
};
