/* eslint-disable import/no-duplicates */
import describe from 'kape';
import { run } from 'kape';

describe('Kape Describe', run, it => {
    it('should create a test suite', given =>
        given([['Suite With No Tests', () => {}, () => {}]]));

    it('should handle a promise test', given =>
        given([
            [
                'Suite For a Promise',
                str => Promise.resolve(str),
                testedIt =>
                    testedIt('should do things', testedGiven =>
                        testedGiven([['hello']]),
                    ),
            ],
        ]));
});

describe('Kape Another Describe', run, it => {
    it('should create a test suite', given =>
        given([
            ['First Suite with No Tests', () => {}, () => {}],
            ['Another Suite with No Tests', () => {}, () => {}],
        ]));
});
