import describe from 'kape';
import renderer from 'renderking';

describe('Render King without Style', renderer(), snapshot => {
    snapshot(
        [],
        [''],
        ['Testing'],
        ['<bar/>Testing'],
        ['Testing<bar/>'],
        ['<bar/>Testing<bar/>'],
    );
});

describe(
    'RenderKing Styler with value style',
    renderer({ bar: { value: '────' } }),
    snapshot => {
        snapshot(
            ['Testing'],
            ['<bar/>Testing'],
            ['Testing<bar/>'],
            ['<bar/>Testing<bar/>'],
            ['Text with <bar/> random <bar/> bars that exist here <bar/>.'],
        );
    },
);
