local operate = require('rx.operator')
local observer = require('rx.observer')

return function(projection)
    return operate(function(source, destination)
        local values = {}

        source.subscribe(
            observer(
                destination,
                function(key)
                    if not values[key] then
                        values[key] = true
                        destination.next(key)
                    end
                end
            )
        )
    end)
end
