local expect, printed = require'rx-test'()
local rx, _ = require'rx'()

describe('Sequence Creation Operator', function()
    describe('sequence()', function()

        it('is included in rx object', function()
            assert.no.is_nil(rx.sequence)
        end)

        it('handles a start to last', function()
            local test_ = rx.sequence(3)
            --
            expect(test_).to.produce(0, 1, 2)
        end)

        it('handles a start to last in reverse', function()
            local test_ = rx.sequence(5, 3)
            --
            expect(test_).to.produce(5, 6, 7)
        end)

    end)
end)
