local create = require('rx.observable.create')

return function(start, count)
    if (not count) then
        count = start
        start = 0
    end

    local final = count + start

    return create(function(destination)
        local iterator = start

        while (iterator < final) do
            destination.next(iterator)
            iterator = iterator + 1
        end

        destination.complete()
    end)
end
