local create = require('rx.observable.create')

return function()
    local operator = function(source)
        return create(function(destination)
            source.subscribe(
                function(...)
                    print(...)
                    destination.next(...)
                end,
                destination.error,
                destination.complete
            )
            return function()
                destination.complete()
            end
        end)
    end

    return operator
end
