local operate = require('rx.operators.operate')
local createObserver = require('rx.observer.create')

return function(projection)
    return operate(function(source, destination)
        source.subscribe(
            createObserver(
                destination,
                function(...)
                    if (projection) then
                        destination.next(projection(...))
                    else
                        destination.next(...)
                    end
                end
            )
        )
    end)
end
