local create = require('rx.observable.create')

return function(projection)
    if (not projection) then
        return false
    end

    return function(source, destination)
        source.subscribe(
            function(...)
                destination.next(projection(...))
            end,
            destination.error,
            destination.complete
        )
        return function()
            destination.complete()
        end
    end
end
