local create = require('rx.observable.create')

return function(count)
    local operator = function(source, destination)
        local seen = 0
        source.subscribe(
            function(...)
                destination.next(...)
                seen = seen + 1

                if (seen >= count) then
                    destination.complete()
                end
            end,
            destination.error,
            destination.complete
        )
        return function()
            destination.complete()
        end
    end

    return count >= 1 and operator or false
end
