local expect = require('rx-test')

local rx = require('rx')
local from = require('rx.observable.from')

describe('From Creation Operator', function()
    describe('from()', function()
        it('is included in rx object', function()
            assert.no.is_nil(rx.observable.from)
        end)

        it('works with a basic array', function()
            local test_ = from({'hello', 'world'})

            expect(test_).to.produce('hello', 'world')
        end)

        it('works with a hashtable, only sending values', function()
            local test_ = from({
                hello = 'world',
                goodbye = 'world',
            })

            expect(test_).to.produce('world', 'world')
        end)

        it('works with a hashtable, with keys', function()
            --TODO
            -- Allow keys from a hashtable for from
        end)
    end)
end)
