local expect, printed = require'rx-test'()

local rx, _ = require'rx'()
local of = require('rx.observable.of')

describe('Of Creation Operator', function()
    describe('of()', function()
        it('is included in rx object', function()
            assert.no.is_nil(rx.observable.of)
        end)

        it('works with a basic string', function()
            local test_ = of('hello')

            expect(test_).to.produce('hello')
        end)

        it('does not send nil values', function()
            local test_ = of('hello', nil, 'world')

            expect(test_).to.produce('hello', 'world')
        end)

        it('works with lots of stuff', function()
            local test_ = of('hello', 23, 'blah', nil, '12', 12)

            expect(test_).to.produce('hello', 23, 'blah', '12', 12)
        end)
    end)
end)
