local expect, printed = require'rx-test'()
local rx, _ = require'rx'()

describe('Log Operator', function()
    describe('log()', function()
        it('is included in rx object', function()
            assert.no.is_nil(_.log)
        end)

        it('handles single value', function()
            local test_ = rx.of('hello').pipe(_.log())

            expect(test_).to.produce('hello')
            printed(1)
        end)

        it('handles multiple values', function()
            local test_ = rx.from({'hello', 'world', 'today'}).pipe(_.log())

            expect(test_).to.produce('hello', 'world', 'today')
            printed(3)
        end)

        it('handles double log operators', function()
            local test_ = rx.of('hello').pipe(_.log(), _.log())

            expect(test_).to.produce('hello')
            printed(2)
        end)

        it('allows a table of operators', function()
            local test_ = rx.of('hello').pipe({_.log(), _.log()})

            expect(test_).to.produce('hello')
            printed(2)
        end)
    end)
end)
