local expect, printed = require('rx-test')()

local rx = require('rx')
local of = require('rx.observable.of')
local from = require('rx.observable.from')
local map = require('rx.operators.map')

describe('Map Operator', function()
    describe('map()', function()
        it('is included in rx object', function()
            assert.no.is_nil(rx.operators.map)
        end)

        it('maps with nothing is bypassed', function()
            local test_ = of('hello')

            expect(test_.pipe(map())).to.produce('hello')
        end)

        it('handles single value', function()
            local test_ = of('world')

            expect(test_.pipe({
                map(function(str) return 'hello ' .. str end),
            })).to.produce('hello world')
        end)

        it('handles multiple values', function()
            local test_ = from({'world', 'folks', 'people'})

            expect(test_.pipe({
                map(function(str) return 'hello ' .. str end),
            })).to.produce('hello world', 'hello folks', 'hello people')
        end)
    end)
end)
