local util = {}

util.noop = function() end

util.printError = function(err) print(err) end

util.isTrue = function(val)
    if (not val) then
        return false
    end

    if (val == '') then
        return false
    end

    if (val == 0) then
        return false
    end

    return true
end

util.isFunction = function(val)
    if type(val) == 'function' then
        return true
    end
end

util.filter = function(t, filterIter)
    local out = {}

    for k, v in pairs(t) do
        if filterIter(v, k, t) then table.insert(out, v) end
    end

    return out
end

util.onlyTrue = function(table)
    return util.filter(table, util.isTrue)
end

util.onlyFunctions = function(table)
    return util.filter(table, util.isFunction)
end

return util
